package main

import (
	"fmt"
)

func pushLastN(lastN []rune, n int, c rune) []rune {
	lastN = append(lastN, c)
	if len(lastN) > n {
		// drop first
		lastN = lastN[1:]
	}
	return lastN
}

func contains(runes []rune, c rune) int {
	for i, r := range runes {
			if c == r {
					return i
			}
	}
	return -1
}

func determineStartPacketMarker(datastream string) int {	
	last3 := make([]rune, 0)
	for i, c := range datastream {
		//fmt.Printf("%d: %c\n", i, c)
		if (len(last3) > 0) {
			loc := contains(last3, c)
			if loc >= 0 {
				last3 = last3[loc+1:]
			}
			if (len(last3) ==3 ) {
				return i+1
			}
		}
		last3 = pushLastN(last3, 3, c)
	}
	return 0
}

func determineStartMessageMarker(datastream string) int {	
	last14 := make([]rune, 0)
	for i, c := range datastream {
		if (len(last14) > 0) {
			loc := contains(last14, c)
			if loc >= 0 {
				last14 = last14[loc+1:]
			}
			if (len(last14) ==13 ) {
				return i+1
			}
		}
		last14 = pushLastN(last14, 14, c)
	}
	return 0
}

func main() {
	fmt.Printf("Challenge Packet chars processed: %d\n", determineStartPacketMarker(""))

	fmt.Printf("Challenge Message chars processed: %d\n", determineStartMessageMarker(""))
}
