package main

import (
	"testing"
)

func Test_determineStartPacketMarker(t *testing.T) {
	t.Run("with example input #1", func(t *testing.T) {
		got := determineStartPacketMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb")	
		want := 7		
		if got != want {
			t.Errorf("got %d want %d", got, want)
		}
	})
	t.Run("with example input #2", func(t *testing.T) {
		got := determineStartPacketMarker("bvwbjplbgvbhsrlpgdmjqwftvncz")	
		want := 5		
		if got != want {
			t.Errorf("got %d want %d", got, want)
		}
	})
	t.Run("with example input #3", func(t *testing.T) {
		got := determineStartPacketMarker("nppdvjthqldpwncqszvftbrmjlhg")	
		want := 6		
		if got != want {
			t.Errorf("got %d want %d", got, want)
		}
	})
	t.Run("with example input #4", func(t *testing.T) {
		got := determineStartPacketMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")	
		want := 10		
		if got != want {
			t.Errorf("got %d want %d", got, want)
		}
	})
	t.Run("with example input #5", func(t *testing.T) {
		got := determineStartPacketMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")	
		want := 11	
		if got != want {
			t.Errorf("got %d want %d", got, want)
		}
	})
	t.Run("with example challenge input", func(t *testing.T) {
		got := determineStartPacketMarker("")	
		want := 1175	
		if got != want {
			t.Errorf("got %d want %d", got, want)
		}
	})
}

